const getContent = (payload) => {
  switch (payload.type) {
    case "USER_INVITED_TO_PROJECT":
      return `${payload?.data?.inviteUser?.name} đã mời bạn vào dự án "${payload.data.project.name}"`;
    case "USER_REMOVED_FROM_PROJECT":
      return `${payload?.data?.removeUser?.name} đã xóa bạn khỏi dự án "${payload.data.project.name}"`;
    case "USER_MENTIONED_IN_TICKET":
      return `${payload?.data?.inviteUser?.name} đã nhắc bạn trong mô tả công việc "${payload.data.ticket.ticketCode}"`;
    case "USER_MENTIONED_IN_COMMENT":
      return `${payload?.data?.inviteUser?.name} đã nhắc bạn trong bình luận công việc "${payload.data.ticket.ticketCode}"`;
    case "USER_ASSIGNED_TO_TICKET":
      return `${payload?.data?.assigningUser?.name} đã giao cho bạn công việc "${payload.data.ticket.ticketCode}"`;
    case "USER_OWN_TICKET":
      return `${payload?.data?.assigningUser?.name} đã gắn bạn là người giao công việc trong "${payload.data.ticket.ticketCode}"`;
    default:
      return "Unknown notification type";
  }
};

export { getContent };
