import React from "react";

const Notifications = ({ notification }) => {
  switch (notification.payload.type) {
    case "USER_INVITED_TO_PROJECT":
      return (
        <div>
          <p>
            {notification.payload.data.inviteUser.name} đã mời bạn vào dự án "
            {notification.payload.data.project.name}"
          </p>
        </div>
      );
    case "USER_REMOVED_FROM_PROJECT":
      return (
        <div>
          <p>
            {notification.payload.data.removeUser.name} đã xóa bạn khỏi dự án "
            {notification.payload.data.project.name}"
          </p>
        </div>
      );
    case "USER_MENTIONED_IN_TICKET":
      return (
        <div>
          <p>
            {notification.payload.data.invitedUser.name} đã nhắc bạn trong mô tả
            công việc "{notification.payload.data.ticket.ticketCode}"
          </p>
        </div>
      );
    case "USER_MENTIONED_IN_COMMENT":
      return (
        <div>
          <p>
            {notification.payload.data.invitedUser.name} đã nhắc bạn trong bình
            luận công việc "{notification.payload.data.ticket.ticketCode}"
          </p>
        </div>
      );
    case "USER_ASSIGNED_TO_TICKET":
      return (
        <div>
          <p>
            {notification.payload.data.assigningUser.name} đã giao cho bạn công
            việc "{notification.payload.data.ticket.ticketCode}"
          </p>
        </div>
      );
    case "USER_OWN_TICKET":
      return (
        <div>
          <p>
            {notification.payload.data.assigningUser.name} đã gắn bạn là người
            giao việc trong "{notification.payload.data.ticket.ticketCode}"
          </p>
        </div>
      );
    default:
      return null;
  }
};

export default Notifications;
