import Typography from "@mui/material/Typography";
import { makeStyles } from "@mui/styles";
const getContent = (payload) => {
  const useStyles = makeStyles({
    customTypography: {
      color: "#333",
      fontSize: "16px",
      fontWeight: "bold",
    },
  });
  const classes = React.useStyles();
  switch (payload.type) {
    case "USER_INVITED_TO_PROJECT":
      return (
        <Typography variant="body1" display="inline">
          <Typography variant="h6" display="inline">
            {payload?.data?.inviteUser?.name}
          </Typography>{" "}
          đã mời bạn vào dự án "
          <Typography variant="h6" display="inline">
            {payload.data.project.name}
          </Typography>
          "
        </Typography>
      );
    case "USER_REMOVED_FROM_PROJECT":
      return (
        <Typography variant="body1" display="inline">
          <Typography variant="h6" display="inline">
            {payload?.data?.removeUser?.name}
          </Typography>{" "}
          đã xóa bạn khỏi dự án "
          <Typography variant="h6" display="inline">
            {payload.data.project.name}
          </Typography>
          "
        </Typography>
      );
    case "USER_MENTIONED_IN_TICKET":
      return (
        <Typography variant="body1" display="inline">
          <Typography variant="h6" display="inline">
            {payload?.data?.inviteUser?.name}
          </Typography>{" "}
          đã nhắc bạn trong mô tả công việc "
          <Typography variant="h6" display="inline">
            {payload.data.ticket.ticketCode}
          </Typography>
          "
        </Typography>
      );
    case "USER_MENTIONED_IN_COMMENT":
      return (
        <Typography variant="body1" display="inline">
          <Typography variant="h6" display="inline">
            {payload?.data?.inviteUser?.name}
          </Typography>{" "}
          đã nhắc bạn trong bình luận công việc "
          <Typography variant="h6" display="inline">
            {payload.data.ticket.ticketCode}
          </Typography>
          "
        </Typography>
      );
    case "USER_ASSIGNED_TO_TICKET":
      return (
        <Typography variant="body1" display="inline">
          <Typography variant="h6" display="inline">
            {payload?.data?.assigningUser?.name}
          </Typography>{" "}
          đã giao cho bạn công việc "
          <Typography variant="h6" display="inline">
            {payload.data.ticket.ticketCode}
          </Typography>
          "
        </Typography>
      );
    case "USER_OWN_TICKET":
      return (
        <Typography variant="body1" display="inline">
          <Typography variant="h6" display="inline">
            {payload?.data?.assigningUser?.name}
          </Typography>{" "}
          đã gắn bạn là người giao công việc trong "
          <Typography variant="h6" display="inline">
            {payload.data.ticket.ticketCode}
          </Typography>
          "
        </Typography>
      );
    default:
      return "Unknown notification type";
  }
};

export { getContent };
