import React, { useState, useEffect } from "react";
import { getNotifications, getUserAvatar } from "../../API";
import Notifications from "../Notifications/index";
const ListNotifications = () => {
  const [notifications, setNotifications] = useState([]);
  const [avatar, setAvatar] = useState("");
  const [message, setMessage] = useState("");
  useEffect(() => {
    const fetchNotifications = async () => {
      try {
        // Gọi API để lấy danh sách thông báo
        const notificationData = await getNotifications();
        setNotifications(notificationData.data);

        // Duyệt qua mỗi thông báo để lấy avatar của người tạo
        const avatarPromises = notificationData.data.map(
          async (notification) => {
            try {
              const avatarData = await getUserAvatar(
                notification.createdUserId
              );
              return { ...notification, avatar: avatarData.avatarUrl };
            } catch (error) {
              console.error("Error fetching user avatar:", error);
              return { ...notification, avatar: null };
            }
          }
        );

        // Đợi tất cả các promise hoàn thành và cập nhật thông báo với avatar
        const notificationsWithAvatar = await Promise.all(avatarPromises);
        setNotifications(notificationsWithAvatar);
      } catch (error) {
        console.error("Error fetching notifications:", error);
      }
    };

    fetchNotifications();
  }, []);

  return (
    <div>
      <h2>Notifications</h2>
      <ul>
        {notifications.map((notification) => {
          return (
            <Notifications key={notification.id} notification={notification} />
          );
          // <li key={notification.id}>{/* Display notification details */}</li>;
        })}
      </ul>
    </div>
  );
};

export default ListNotifications;
