import axios from "axios";

const BASE_URL = "https://dev-api.lovicogroup.com/api/v1";

const getToken = () => {
  return "Bearer eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCIsImtpZCI6ImNXQjE0Zk40WGpxWm5UWVdFdnN2bCJ9..YTCoBaP5ce3vOj7widkgLM1YFMlsfGpuwysOIz0YxNBrdrBZ0xfFYEnrqPlCzC5Rdx-qDM_AmZobUX07poISO6BWQKysj6X3bGfhoMrG4DVAb26gzXvhg-4JuJbz3fuu9QWBlw3U7YVfCHV2aHG38iMwPzwafDIaM2_Npny1pJQQcHt5stxRBEcsAhYa-_aLFOrToqPpT8co_VVMzXC6d35L9RIuqvFlr3l6OQJs38kznjylBSHuLHjNhbY0992AaGMmhkF7hVwY1_EBPosqNNYp44DdkNn8frxuA3F67p1AIFL6D7_7stpY1nWm9EQLN0HRWfQrBSIL4AJChQee4A";
};

export const getNotifications = async (limit = 10, page = 1) => {
  try {
    const response = await axios.get(
      `${BASE_URL}/notifications?limit=${limit}&page=${page}`,
      {
        headers: {
          Authorization: getToken(),
        },
      }
    );
    return response.data;
  } catch (error) {
    console.error("Error fetching notifications:", error);
    throw error;
  }
};

export const markNotificationAsRead = async (notificationId) => {
  try {
    await axios.put(
      `${BASE_URL}/notifications/user-read-notification/${notificationId}`,
      null,
      {
        headers: {
          Authorization: getToken(),
        },
      }
    );
  } catch (error) {
    console.error("Error marking notification as read:", error);
    throw error;
  }
};

export const getUserAvatar = async (createdUserId) => {
  try {
    const response = await axios.get(
      `${BASE_URL}/user-avatar/${createdUserId}/thumbnail`,
      {
        headers: {
          Authorization: getToken(),
        },
      }
    );
    return response.data.url;
  } catch (error) {
    console.error("Error fetching user avatar:", error);
    throw error;
  }
};
