import React, { useState, useEffect } from "react";
import axios from "axios";

const ListNotifications = () => {
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    const fetchNotifications = async () => {
      try {
        // Mã thông báo (bearer token)
        const token =
          "eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCIsImtpZCI6ImNXQjE0Zk40WGpxWm5UWVdFdnN2bCJ9..YTCoBaP5ce3vOj7widkgLM1YFMlsfGpuwysOIz0YxNBrdrBZ0xfFYEnrqPlCzC5Rdx-qDM_AmZobUX07poISO6BWQKysj6X3bGfhoMrG4DVAb26gzXvhg-4JuJbz3fuu9QWBlw3U7YVfCHV2aHG38iMwPzwafDIaM2_Npny1pJQQcHt5stxRBEcsAhYa-_aLFOrToqPpT8co_VVMzXC6d35L9RIuqvFlr3l6OQJs38kznjylBSHuLHjNhbY0992AaGMmhkF7hVwY1_EBPosqNNYp44DdkNn8frxuA3F67p1AIFL6D7_7stpY1nWm9EQLN0HRWfQrBSIL4AJChQee4A";

        // Cấu hình Axios
        const axiosConfig = {
          baseURL: "https://dev-api.lovicogroup.com/api/v1",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        };

        // Gửi yêu cầu GET để lấy danh sách thông báo
        const response = await axios.get("/notifications", axiosConfig);

        // Cập nhật state với dữ liệu thông báo từ phản hồi
        setNotifications(response.data);
      } catch (error) {
        console.error("Error:", error);
      }
    };

    // Gọi hàm fetchNotifications khi component được mount
    fetchNotifications();
  }, []); // useEffect sẽ chỉ chạy một lần sau khi component được mount

  return (
    <div>
      <h2>Notifications</h2>
      <ul>
        {notifications.map((notification) => (
          <li key={notification.id}>{notification.message}</li>
        ))}
      </ul>
    </div>
  );
};

export default ListNotifications;
