import axios from "axios";
import React, { useEffect, useState } from "react";

const Notifications = ({ notification }) => {
  const [avatar, setAvatar] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchAvatar = async () => {
      try {
        const response = await axios.get(
          `https://dev-api.lovicogroup.com/api/v1/notifications/user-avatar/${notification.createdUserId}/thumbnail`,
          {
            headers: {
              Authorization: "Bearer your_bearer_token_here",
            },
          }
        );
        setAvatar(response.data);
      } catch (error) {
        console.error("Error fetching avatar:", error);
      }
    };

    fetchAvatar();
  });
  return (
    <li key={notification.id}>
      {notification.payload &&
        notification.payload.data &&
        notification.payload.data.project && (
          <div>
            <strong>{notification.payload.type}</strong>:{" "}
            {notification.payload.data.project.name}
          </div>
        )}
    </li>
  );
};

export default Notifications;
