import React, { useState, useEffect } from "react";
import { getNotifications, getUserAvatar } from "../../API";
import Notifications from "../Notifications/index";
const ListNotifications = () => {
  const [notifications, setNotifications] = useState([]);
  const [avatar, setAvatar] = useState("");
  const [message, setMessage] = useState("");
  useEffect(() => {
    const fetchNotifications = async () => {
      try {
        const data = await getNotifications();
        setNotifications(data.data);
      } catch (error) {
        console.error("Error fetching notifications:", error);
      }
    };

    fetchNotifications();
  }, []);

  useEffect(() => {
    const fetchAvatar = async () => {
      try {
        const data = await getUserAvatar(notification.createdUserId);
        setAvatar(data.data);
      } catch (error) {
        console.error("Error fetching avatar:", error);
        setMessage("Error fetching avatar");
      }
    };

    fetchAvatar();
  }, [notification.createdUserId]);

  return (
    <div>
      <h2>Notifications</h2>
      <ul>
        {notifications.map((notification) => {
          return (
            <Notifications key={notification.id} notification={notification} />
          );
          // <li key={notification.id}>{/* Display notification details */}</li>;
        })}
      </ul>
    </div>
  );
};

export default ListNotifications;
