import React, { useEffect, useState } from "react";
import { getUserAvatar } from "../../API";
const Notifications = ({ notification }) => {
  const [avatar, setAvatar] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchAvatar = async () => {
      try {
        const data = await getUserAvatar(notification.createdUserId);
        setAvatar(data.data);
      } catch (error) {
        console.error("Error fetching avatar:", error);
        setMessage("Error fetching avatar");
      }
    };

    fetchAvatar();
  }, [notification.createdUserId]);
  return (
    <li key={notification.id}>
      {notification.payload &&
        notification.payload.data &&
        notification.payload.data.project && (
          <div>
            <strong>{notification.payload.type}</strong>:{" "}
            {notification.payload.data.project.name}
          </div>
        )}
    </li>
  );
};

export default Notifications;
