import React, { useState } from "react";
import { Avatar, Menu, MenuItem, Tooltip } from "@mui/material";
import IconButton from "@mui/material/IconButton";
import { useTranslation } from "react-i18next";
import "../../../locales/i18n";
const Header = () => {
  const [anchorEl, setAnchorEl] = useState(null);
  const { t, i18n } = useTranslation();
  const [avatarSrc, setAvatarSrc] = useState("/path/to/vn/avatar.jpg"); // Đường dẫn đến avatar mặc định của ngôn ngữ VN
  const [currentLanguage, setCurrentLanguage] = useState("vn");

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
    setCurrentLanguage(lng);
    setAnchorEl(null);
    // Cập nhật avatar tương ứng với ngôn ngữ mới
    setAvatarSrc(
      lng === "vn" ? "/path/to/vn/avatar.jpg" : "/path/to/en/avatar.jpg"
    );
  };
  const open = Boolean(anchorEl);
  const handleClose = () => {
    setAnchorEl(null);
  };
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  return (
    <>
      <Tooltip title="Languages">
        <IconButton
          size="large"
          aria-label="language"
          color="inherit"
          onClick={handleClick}
          aria-controls={open ? "language-menu" : undefined}
          aria-haspopup="true"
          aria-expanded={open ? "true" : undefined}
        >
          <Avatar
            sx={{ width: "22px", height: "22px" }}
            src={avatarSrc}
            alt=""
          />
        </IconButton>
      </Tooltip>
      <Menu
        elevation={0}
        id="language-menu"
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
      >
        <MenuItem onClick={() => changeLanguage("en")}>English</MenuItem>
        <MenuItem onClick={() => changeLanguage("vn")}>VietNam</MenuItem>
      </Menu>
    </>
  );
};

export default Header;
