import React, { useEffect, useState } from "react";
const Notifications = ({ notification }) => {
  switch (notification.payload.type) {
    case "USER_INVITED_TO_PROJECT":
      return (
        <div>
          <p>
            {notification.payload.data.inviteUser.name} đã mời bạn vào dự án "
            {notification.payload.data.project.name}"
          </p>
          {/* <button onClick={() => markAsRead(notification.id)}>
            Mark as read
          </button> */}
        </div>
      );
    case "USER_REMOVED_FROM_PROJECT":
      return (
        <div>
          <p>
            {notification.payload.data.removeUser.name} đã xóa bạn khỏi dự án "
            {notification.payload.data.project.name}"
          </p>
          {/* <button onClick={() => markAsRead(notification.id)}>
            Mark as read
          </button> */}
        </div>
      );
    // handle other types similarly
    default:
      return null;
  }
  return (
    <li key={notification.id}>
      {notification.payload &&
        notification.payload.data &&
        notification.payload.data.project && (
          <div>
            <strong>{notification.payload.type}</strong>:{" "}
            {notification.payload.data.project.name}
          </div>
        )}
    </li>
  );
};

export default Notifications;
