import React, { useEffect, useState } from "react";
import { getUserAvatar } from "../../API";
const Notifications = ({ notification, markAsRead }) => {
  const [avatarUrl, setAvatarUrl] = useState(null);

  useEffect(() => {
    const fetchAvatar = async () => {
      try {
        const avatarUrl = await getUserAvatar(notification.createdUserId);
        setAvatarUrl(avatarUrl);
      } catch (error) {
        console.error("Error fetching avatar:", error);
      }
    };
    fetchAvatar();
  }, [notification.createdUserId]);
  const getNotificationClassName = (isRead) => {
    return isRead ? "read" : "unread";
  };

  const handleClick = () => {
    if (!notification.isRead) {
      markAsRead(notification.id);
    }
  };

  const notificationClassName = getNotificationClassName(notification.isRead);

  return (
    <div
      className={`notification-item flex align-items-center ${notificationClassName}`}
      onClick={handleClick}
    >
      {notification.payload.type === "USER_INVITED_TO_PROJECT" && (
        <>
          <div className="notification-img">
            {avatarUrl && <img src={avatarUrl} alt="" />}
          </div>
          <p className="notification-content">
            {notification.payload?.data?.inviteUser?.name} đã mời bạn vào dự án
            "{notification.payload.data.project.name}"
          </p>
        </>
      )}
      {notification.payload.type === "USER_REMOVED_FROM_PROJECT" && (
        <>
          <div className="notification-img">
            {avatarUrl && <img src={avatarUrl} alt="" />}
          </div>
          <p className="notification-content">
            {notification.payload?.data?.removeUser?.name} đã xóa bạn khỏi dự án
            "{notification.payload.data.project.name}"
          </p>
        </>
      )}
      {notification.payload.type === "USER_MENTIONED_IN_TICKET" && (
        <>
          <div className="notification-img">
            {avatarUrl && <img src={avatarUrl} alt="" />}
          </div>
          <p className="notification-content">
            {notification.payload?.data?.inviteUser?.name} đã nhắc bạn trong mô
            tả công việc "{notification.payload.data.ticket.ticketCode}"
          </p>
        </>
      )}
      {notification.payload.type === "USER_MENTIONED_IN_COMMENT" && (
        <>
          <div className="notification-img">
            {avatarUrl && <img src={avatarUrl} alt="" />}
          </div>
          <p className="notification-content">
            {notification.payload?.data?.inviteUser?.name} đã nhắc bạn trong
            bình luận công việc "{notification.payload.data.ticket.ticketCode}"
          </p>
        </>
      )}
      {notification.payload.type === "USER_ASSIGNED_TO_TICKET" && (
        <>
          <div className="notification-img">
            {avatarUrl && <img src={avatarUrl} alt="" />}
          </div>
          <p className="notification-content">
            {notification.payload?.data?.assigningUser?.name} đã giao cho bạn
            công việc "{notification.payload.data.ticket.ticketCode}"
          </p>
        </>
      )}
      {notification.payload.type === "USER_OWN_TICKET" && (
        <>
          <div className="notification-img">
            {avatarUrl && <img src={avatarUrl} alt="" />}
          </div>
          <p className="notification-content">
            {notification.payload?.data?.assigningUser?.name} đã gắn bạn là
            người giao việc trong "{notification.payload.data.ticket.ticketCode}
            "
          </p>
        </>
      )}
    </div>
  );
};

export default Notifications;
