import CustomTypography from "@mui/material/CustomTypography";
import { styled } from "@mui/system";
const CustomCustomTypography = styled(CustomTypography)`
  color: #333;
  font-size: 16px;
  font-weight: bold;
`;
const getContent = (payload) => {
  switch (payload.type) {
    case "USER_INVITED_TO_PROJECT":
      return (
        <CustomTypography variant="body1" display="inline">
          <CustomTypography variant="h6" display="inline">
            {payload?.data?.inviteUser?.name}
          </CustomTypography>{" "}
          đã mời bạn vào dự án "
          <CustomTypography variant="h6" display="inline">
            {payload.data.project.name}
          </CustomTypography>
          "
        </CustomTypography>
      );
    case "USER_REMOVED_FROM_PROJECT":
      return (
        <CustomTypography variant="body1" display="inline">
          <CustomTypography variant="h6" display="inline">
            {payload?.data?.removeUser?.name}
          </CustomTypography>{" "}
          đã xóa bạn khỏi dự án "
          <CustomTypography variant="h6" display="inline">
            {payload.data.project.name}
          </CustomTypography>
          "
        </CustomTypography>
      );
    case "USER_MENTIONED_IN_TICKET":
      return (
        <CustomTypography variant="body1" display="inline">
          <CustomTypography variant="h6" display="inline">
            {payload?.data?.inviteUser?.name}
          </CustomTypography>{" "}
          đã nhắc bạn trong mô tả công việc "
          <CustomTypography variant="h6" display="inline">
            {payload.data.ticket.ticketCode}
          </CustomTypography>
          "
        </CustomTypography>
      );
    case "USER_MENTIONED_IN_COMMENT":
      return (
        <CustomTypography variant="body1" display="inline">
          <CustomTypography variant="h6" display="inline">
            {payload?.data?.inviteUser?.name}
          </CustomTypography>{" "}
          đã nhắc bạn trong bình luận công việc "
          <CustomTypography variant="h6" display="inline">
            {payload.data.ticket.ticketCode}
          </CustomTypography>
          "
        </CustomTypography>
      );
    case "USER_ASSIGNED_TO_TICKET":
      return (
        <CustomTypography variant="body1" display="inline">
          <CustomTypography variant="h6" display="inline">
            {payload?.data?.assigningUser?.name}
          </CustomTypography>{" "}
          đã giao cho bạn công việc "
          <CustomTypography variant="h6" display="inline">
            {payload.data.ticket.ticketCode}
          </CustomTypography>
          "
        </CustomTypography>
      );
    case "USER_OWN_TICKET":
      return (
        <CustomTypography variant="body1" display="inline">
          <CustomTypography variant="h6" display="inline">
            {payload?.data?.assigningUser?.name}
          </CustomTypography>{" "}
          đã gắn bạn là người giao công việc trong "
          <CustomTypography variant="h6" display="inline">
            {payload.data.ticket.ticketCode}
          </CustomTypography>
          "
        </CustomTypography>
      );
    default:
      return "Unknown notification type";
  }
};

export { getContent };
