import React, { useEffect, useState } from "react";
import { getUserAvatar } from "../../API";
const Notifications = ({ notification }) => {
  const [avatar, setAvatar] = useState("");

  useEffect(() => {
    const fetchAvatar = async () => {
      try {
        const data = await getUserAvatar(data.createdUserId);
        setAvatar(data.createdUserId);
      } catch (error) {
        console.error("Error fetching notifications:", error);
      }
    };

    fetchAvatar();
  }, []);
  return (
    <li key={notification.id}>
      {notification.payload &&
        notification.payload.data &&
        notification.payload.data.project && (
          <div>
            <strong>{notification.payload.type}</strong>:{" "}
            {notification.payload.data.project.name}
          </div>
        )}
    </li>
  );
};

export default Notifications;
