import Typography from "@mui/material/Typography";
import { styled } from "@mui/system";
const getContent = (payload) => {
  const CustomTypography = styled(Typography)`
    color: #333;
    font-size: 16px;
    font-weight: bold;
    variant: "h6";
    display: "inline";
  `;
  switch (payload.type) {
    case "USER_INVITED_TO_PROJECT":
      return (
        <Typography variant="body1" display="inline">
          <Typography>{payload?.data?.inviteUser?.name}</Typography> đã mời bạn
          vào dự án "<Typography>{payload.data.project.name}</Typography>"
        </Typography>
      );
    case "USER_REMOVED_FROM_PROJECT":
      return (
        <Typography variant="body1" display="inline">
          <Typography>{payload?.data?.removeUser?.name}</Typography> đã xóa bạn
          khỏi dự án "<Typography>{payload.data.project.name}</Typography>"
        </Typography>
      );
    case "USER_MENTIONED_IN_TICKET":
      return (
        <Typography variant="body1" display="inline">
          <Typography>{payload?.data?.inviteUser?.name}</Typography> đã nhắc bạn
          trong mô tả công việc "
          <Typography>{payload.data.ticket.ticketCode}</Typography>"
        </Typography>
      );
    case "USER_MENTIONED_IN_COMMENT":
      return (
        <Typography variant="body1" display="inline">
          <Typography>{payload?.data?.inviteUser?.name}</Typography> đã nhắc bạn
          trong bình luận công việc "
          <Typography>{payload.data.ticket.ticketCode}</Typography>"
        </Typography>
      );
    case "USER_ASSIGNED_TO_TICKET":
      return (
        <Typography variant="body1" display="inline">
          <Typography>{payload?.data?.assigningUser?.name}</Typography> đã giao
          cho bạn công việc "
          <Typography>{payload.data.ticket.ticketCode}</Typography>"
        </Typography>
      );
    case "USER_OWN_TICKET":
      return (
        <Typography variant="body1" display="inline">
          <Typography>{payload?.data?.assigningUser?.name}</Typography> đã gắn
          bạn là người giao công việc trong "
          <Typography>{payload.data.ticket.ticketCode}</Typography>"
        </Typography>
      );
    default:
      return "Unknown notification type";
  }
};

export { getContent };
