import Typography from "@mui/material/Typography";
import { styled } from "@mui/system";
const CustomTypography = styled(Typography)`
  color: #333;
  font-size: 16px;
  font-weight: bold;
  display:"inline";
  variant="h6"
`;
const getContent = (payload) => {
  switch (payload.type) {
    case "USER_INVITED_TO_PROJECT":
      return (
        <CustomTypography variant="body1">
          <CustomTypography>{payload?.data?.inviteUser?.name}</CustomTypography>{" "}
          đã mời bạn vào dự án "
          <CustomTypography>{payload.data.project.name}</CustomTypography>"
        </CustomTypography>
      );
    case "USER_REMOVED_FROM_PROJECT":
      return (
        <CustomTypography variant="body1">
          <CustomTypography>{payload?.data?.removeUser?.name}</CustomTypography>{" "}
          đã xóa bạn khỏi dự án "
          <CustomTypography>{payload.data.project.name}</CustomTypography>"
        </CustomTypography>
      );
    case "USER_MENTIONED_IN_TICKET":
      return (
        <CustomTypography variant="body1">
          <CustomTypography>{payload?.data?.inviteUser?.name}</CustomTypography>{" "}
          đã nhắc bạn trong mô tả công việc "
          <CustomTypography>{payload.data.ticket.ticketCode}</CustomTypography>"
        </CustomTypography>
      );
    case "USER_MENTIONED_IN_COMMENT":
      return (
        <CustomTypography variant="body1">
          <CustomTypography>{payload?.data?.inviteUser?.name}</CustomTypography>{" "}
          đã nhắc bạn trong bình luận công việc "
          <CustomTypography>{payload.data.ticket.ticketCode}</CustomTypography>"
        </CustomTypography>
      );
    case "USER_ASSIGNED_TO_TICKET":
      return (
        <CustomTypography variant="body1">
          <CustomTypography>
            {payload?.data?.assigningUser?.name}
          </CustomTypography>{" "}
          đã giao cho bạn công việc "
          <CustomTypography>{payload.data.ticket.ticketCode}</CustomTypography>"
        </CustomTypography>
      );
    case "USER_OWN_TICKET":
      return (
        <CustomTypography variant="body1">
          <CustomTypography>
            {payload?.data?.assigningUser?.name}
          </CustomTypography>{" "}
          đã gắn bạn là người giao công việc trong "
          <CustomTypography>{payload.data.ticket.ticketCode}</CustomTypography>"
        </CustomTypography>
      );
    default:
      return "Unknown notification type";
  }
};

export { getContent };
